// A function to determine if this image contains a celebrity.
// Functions can access the msg.result via array indexes
// Watson Visual Recognition v3 API Documentation is post here:
// https://www.ibm.com/smarterplanet/us/en/ibmwatson/developercloud/visual-recognition/api/v3/?node#detect_faces
var i=0;
var Celebrity=0;
var CelebrityName="No one famous detected";

// There might be multiple faces in this image.
// Loop through the faces and determine if Watson has
// detected the identity of at least one celebrity.
while ( msg.result.images[0].faces[i] ) {
    if (msg.result.images[0].faces[i].identity != null) {
        Celebrity=1;
        CelebrityName=msg.result.images[0].faces[i].identity.name;
    }
    i++;
}

msg.payload = CelebrityName;
// if a Celebrity was found, send the Name to output 1
// else send the default "No one famous detected" to output 2
if (Celebrity == 1) {
        return [ msg, null ];
} else {
    return [ null, msg ];
}
